function common_select(){
	$('select.form-control').selectpicker({ 
		container: 'body', 
		style:'btn-select',
		noneSelectedText: '',
	});
	$('select.form-control').on('changed.bs.select', function (e, clickedIndex, isSelected, previousValue) {
	  $(this).parent('.fixedCustom').addClass('has-value');
	});
}



